import React, { useContext } from "react";

import { ThemeContext } from "./Agenda";

const EventDetails = ({ attendees }) => {
    const theme = useContext(ThemeContext);
    return attendees && attendees.length > 1 ? (
        <div className={`${theme.prefix}__event__attendees--expanded`}>
            <h3 className={`${theme.prefix}__event__subtitle`}>Attendees</h3>
            {attendees.map((attendee, key) => {
                return (
                    <li key={key}>
                        <span className={`${theme.prefix}__event__attendee`} />
                        {attendee.display_name ? (
                            <span>{attendee.display_name}</span>
                        ) : null}{" "}
                        {attendee.email ? (
                            <span>{`<${attendee.email}>`}</span>
                        ) : null}
                    </li>
                );
            })}
        </div>
    ) : null;
};

export default EventDetails;
