import React, { useContext } from "react";

import { I18nContext, ThemeContext } from "./Agenda";

const EventMessages = ({ data }) => {
    const theme = useContext(ThemeContext);
    const i18n = useContext(I18nContext);
    return (
        <div className={`${theme.prefix}__event__messages`}>
            <span
                className={`${theme.prefix}__event__message ${theme.prefix}__event__message--transparency`}
            >
                {data.transparency === "transparent"
                    ? i18n.t("transparent")
                    : i18n.t("opaque")}
            </span>
            <span
                className={`${theme.prefix}__event__message ${theme.prefix}__event__message--participation`}
            >
                {data.participation_status === "needs_action"
                    ? i18n.t("needs_action")
                    : null}
            </span>
        </div>
    );
};

export default EventMessages;
