import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { getCalendarColors } from "../../helpers/colors";
import { ThemeContext } from "./Agenda";

const Location = ({ styles, locationString, calIndex, children }) => {
    const theme = useContext(ThemeContext);
    let colors = getCalendarColors(theme.colors, calIndex);
    return (
        <span
            css={css`
                display: inline-block;
                border: 1px solid ${colors.text};
                padding: 3px 5px;
                border-radius: 4px;
                font-size: 0.6em;
                color: ${colors.text};
                ${styles};
            `}
            className={`${theme.prefix}__event__location`}
            title={locationString}
        >
            {children}
        </span>
    );
};

export default Location;
