import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import Event from "./Event";
import ShadowScrollbars from "../generic/ShadowScrollbars";

import { ThemeContext } from "./Agenda";

const TimedEventsList = props => {
    const theme = useContext(ThemeContext);
    return (
        <ShadowScrollbars
            css={css`
                flex-grow: 1;
                height: auto !important; // Hack to avoid Scrollbars injected styles that break the flexbox layout.
                display: flex;
            `}
            className={`${theme.prefix}__list ${theme.prefix}__list--timed`}
        >
            {props.events.length > 0
                ? props.events.map((event, key) => (
                      <Event
                          key={key}
                          current={props.date}
                          data={event}
                          expand={props.expand}
                      />
                  ))
                : null}
        </ShadowScrollbars>
    );
};

export default TimedEventsList;
