import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { ExtrasContext, ThemeContext } from "./AvailabilityRules";

const Slot = ({ slot }) => {
    const [extras, setExtras] = useContext(ExtrasContext);
    const [theme, setTheme] = useContext(ThemeContext);

    let normalBG = slot.available
        ? theme.colors.available
        : theme.colors.unavailable;

    return (
        <div
            css={css`
                display: block;
                height: ${extras.slotHeight.height}px;
                background: ${normalBG};
                transition: background 0.2s;
            `}
            className={`${theme.prefix}__slot-background ${
                theme.prefix
            }__slot-background--${
                slot.available ? `available` : `unavailable`
            }`}
        />
    );
};

export default Slot;
