import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { I18nContext, ThemeContext } from "./AvailabilityRules";
import RemoveButton from "./RemoveButton";

const Calendar = ({ id, remove = false }) => {
    const [theme, setTheme] = useContext(ThemeContext);
    const i18n = useContext(I18nContext);
    return (
        <div
            css={css`
                display: flex;
                align-items: center;
                border-radius: 4px;
                background: ${theme.colors.greyMid};
                margin-right: 8px;
                margin-bottom: 5px;
                font-size: 16px;
                line-height: 1;
                padding: 6px 10px;
                color: ${theme.colors.white};
            `}
            className={`${theme.prefix}__calendars__calendar`}
        >
            {i18n.t("unknown")}
            {remove ? <RemoveButton callback={() => remove(id)} /> : null}
        </div>
    );
};

export default Calendar;
