import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { I18nContext, ThemeContext, StatusContext } from "./AvailabilityViewer";

const EmptyColumn = () => {
    const [theme, setTheme] = useContext(ThemeContext);
    const [status, setStatus] = useContext(StatusContext);
    const i18n = useContext(I18nContext);
    return (
        <div
            css={css`
                width: 100%;
                height: 100%;
                flex-grow: 1;
                display: flex;
                justify-content: center;
                align-items: center;
            `}
            className={`${theme.prefix}__column-body ${theme.prefix}__column-body--empty`}
        >
            <span className={`${theme.prefix}__column-message visuallyhidden`}>
                {status.loading ? null : i18n.t("no_slots_found")}
            </span>
        </div>
    );
};

export default EmptyColumn;
