import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { getMonthsDescription } from "../../helpers/slots";

import { ThemeContext, SlotsContext } from "./AvailabilityViewer";

const MonthLabels = () => {
    const [theme, setTheme] = useContext(ThemeContext);
    const slots = useContext(SlotsContext);

    const monthLabel = getMonthsDescription(slots);
    return (
        <div
            css={css`
                display: flex;
                justify-content: stretch;
                position: relative;
            `}
            className={`${theme.prefix}__month-labels`}
        >
            <div
                css={css`
                    width: ${theme.sizes.labelWidth}px;
                    flex-grow: 0;
                    flex-shrink: 0;
                    @media (max-width: 650px) {
                        display: none;
                    }
                `}
            />
            <div
                css={css`
                    font-size: 20px;
                `}
                className={`
                    ${theme.prefix}__month-label`}
            >
                {monthLabel}
            </div>
        </div>
    );
};

export default MonthLabels;
