import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { ThemeContext, StatusContext } from "./CalendarSync";
import AddToggle from "./AddToggle";
import EditToggle from "./EditToggle";

const Footer = ({ connected, syncRequired, relinkUrl = false }) => {
    const theme = useContext(ThemeContext);
    const [status, setStatus] = useContext(StatusContext);

    return (
        <div
            css={css`
                display: flex;
                align-items: center;
                justify-content: space-between;
                margin-top: 10px;
                @media (max-width: 650px) {
                    flex-wrap: wrap;
                }
            `}
            className={`${theme.prefix}__footer`}
        >
            {!status.editing && !status.singleProfile ? (
                <AddToggle />
            ) : (
                <span />
            )}
            {!status.adding ? <EditToggle /> : null}
        </div>
    );
};

export default Footer;
