import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { ThemeContext } from "./CalendarSync";
import Active from "./Active";
import Inactive from "./Inactive";
import Pending from "./Pending";

const ProfileStatus = ({ connected, syncRequired, relinkUrl = false }) => {
    const theme = useContext(ThemeContext);

    const showRelink = !connected && relinkUrl;
    const showActive = connected && !syncRequired;
    const showPending = syncRequired;
    return (
        <div
            css={css`
                @media (max-width: 380px) {
                    width: 100%;
                    // margin-top: 4px;
                    // margin-bottom: 4px;
                }
            `}
            className={`${theme.prefix}__provider-status`}
        >
            {showRelink ? <Inactive url={relinkUrl} /> : null}
            {showPending ? <Pending /> : null}
            {showActive ? <Active /> : null}
        </div>
    );
};

export default ProfileStatus;
