import React, { useContext } from "react";
import { css, jsx } from "@emotion/core";

import { ThemeContext } from "./CalendarSync";

import { getProviderIcon } from "../../helpers/utils.CalendarSync";

const ProviderLogo = ({ provider, fill = false, size = "20" }) => {
    const theme = useContext(ThemeContext);
    const providerIcon = getProviderIcon(provider, fill);
    return (
        <svg
            className={`${theme.prefix}__provider-icon ${theme.prefix}__provider-icon--${provider}`}
            css={css`
                width: ${size}px;
                height ${size}px;
                margin-right: 10px;
                ${fill ? "" : `path {fill: ${theme.colors.greyMid};}`}
            `}
            viewBox="0 0 20 23"
            xmlns="http://www.w3.org/2000/svg"
        >
            {providerIcon}
        </svg>
    );
};

export default ProviderLogo;
