import React from "react";
import { css, jsx } from "@emotion/core";

import { buttonReset } from "../../styles/utils";
import colors from "../../styles/colors";

const Button = ({ className = "button", styles, onClick, children }) => {
    let buttonStyles = css`
        ${buttonReset};
        cursor: pointer;
        display: inline-block;
        padding: 10px;
        position: relative;
        font-size: inherit;
        transition: background 0.2s;
        color: inherit;
        ${styles};
    `;

    return (
        <button
            type="button"
            className={className}
            onClick={onClick}
            css={buttonStyles}
        >
            {children}
        </button>
    );
};

export default Button;
