import React from "react";
import { css, jsx } from "@emotion/core";

import { buttonReset } from "../../styles/utils";
import colors from "../../styles/colors";

const IconButton = ({ label, icon, callback, styles = `` }) => (
    <button
        type="button"
        onClick={callback}
        css={css`
            ${buttonReset};
            display: inline-block;
            padding: 0.5em;
            padding-right: 2em;
            position: relative;
            font-size: 1;
            font-weight: bold;
            line-height: 1;
            ${styles};
        `}
    >
        <span>{label}</span>
        <svg
            css={css`
                position: absolute;
                top: 1em;
                right: 1em;
                // margin-top: 0.2em;
                width: 1.2em;
                height: 1.2em;
                fill: ${colors.black};
                margin-left: 0.5em;
                transform: translate(50%, -50%);
                pointer-events: none;
            `}
        >
            <use xlinkHref={`#${icon}`} />
        </svg>
    </button>
);

export default IconButton;
