const getTranslationsFromJson = (locales, contexts) =>
    locales.reduce(
        (acc, locale) => ({
            ...acc,
            [locale]: contexts.reduce((acc, context) => {
                const {
                    [context]: contextObj
                } = require(`../translations/${locale}/${context}.json`);
                return {
                    ...acc,
                    [context]: contextObj
                };
            }, {})
        }),
        {}
    );

const contexts = [
    "core",
    "agenda",
    "availability_rules",
    "availability_viewer",
    "calendar_sync",
    "slot_picker",
    "time_zones"
];
const locales = ["de", "en", "es", "fr", "it", "nl"];

export const translations = getTranslationsFromJson(locales, contexts);
