import React from "react";
import { storiesOf } from "@storybook/react";
import { action } from "@storybook/addon-actions";

import "../src/js/icons";

import Agenda from "../src/js/components/Agenda/Agenda";
import SlotPicker from "../src/js/components/SlotPicker/SlotPicker";
import Message from "../src/js/components/Agenda/Message";
import Loading from "../src/js/components/generic/Loading";

import TransitionToggler from "./story-temps/TransitionToggler";
import Wrapper from "./story-temps/Wrapper";
import { AVAILABLE_PERIODS, AVAILABLE_DAYS } from "./story-temps/mock-data";

storiesOf("Agenda Component", module).add("demo mode", () => (
    // <Wrapper layout={[400, 500]}>
    <Wrapper>
        <Agenda options={{ demo: true }} />
    </Wrapper>
));

storiesOf("SlotPicker component", module)
    .add("full", () => (
        <Wrapper>
            <SlotPicker
                options={{ demo: true }}
                view={"days"}
                days={AVAILABLE_DAYS}
                slots={AVAILABLE_PERIODS}
            />
        </Wrapper>
    ))
    .add("slots", () => (
        <Wrapper>
            <SlotPicker
                options={{ demo: true }}
                view={"slots"}
                days={AVAILABLE_DAYS}
                slots={AVAILABLE_PERIODS}
            />
        </Wrapper>
    ))
    .add("confirmation", () => (
        <Wrapper>
            <SlotPicker
                options={{ demo: true }}
                view={"confirm"}
                days={AVAILABLE_DAYS}
                slots={AVAILABLE_PERIODS}
            />
        </Wrapper>
    ))
    .add("empty", () => (
        <Wrapper>
            <SlotPicker options={{ demo: true }} view={"days"} days={[]} />
        </Wrapper>
    ))
    .add("loading", () => (
        <Wrapper>
            <SlotPicker options={{ demo: true }} view={"days"} loading={true} />
        </Wrapper>
    ));

storiesOf("Message", module).add("with text", () => (
    <Message message={"a message to be shown"} />
));

storiesOf("Loading indicator", module)
    .add("active", () => (
        <Loading loading={true} onClick={action("clicked")}>
            <p>Some stuff that's loading</p>
        </Loading>
    ))
    .add("inactive", () => (
        <Loading loading={false} onClick={action("clicked")}>
            <p>Some stuff that's loading</p>
        </Loading>
    ));

storiesOf("Transitions", module)
    .add("forwards", () => (
        <Wrapper>
            <TransitionToggler direction="forwards" />
        </Wrapper>
    ))
    .add("backwards", () => (
        <Wrapper>
            <TransitionToggler direction="backwards" />
        </Wrapper>
    ))
    .add("upwards", () => (
        <Wrapper>
            <TransitionToggler direction="upwards" />
        </Wrapper>
    ))
    .add("downwards", () => (
        <Wrapper>
            <TransitionToggler direction="downwards" />
        </Wrapper>
    ));
