import * as slotsRulesHelpers from "../src/js/helpers/slots.rules";

describe("Slots Rules helpers", () => {
    // getWeekDays
    it("returns array of week days", () => {
        const result = slotsRulesHelpers.getWeekDays();
        const expectedResult = [
            "sunday",
            "monday",
            "tuesday",
            "wednesday",
            "thursday",
            "friday",
            "saturday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a monday", () => {
        const result = slotsRulesHelpers.getWeekDays("monday");
        const expectedResult = [
            "monday",
            "tuesday",
            "wednesday",
            "thursday",
            "friday",
            "saturday",
            "sunday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a tuesday", () => {
        const result = slotsRulesHelpers.getWeekDays("tuesday");
        const expectedResult = [
            "tuesday",
            "wednesday",
            "thursday",
            "friday",
            "saturday",
            "sunday",
            "monday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a wednesday", () => {
        const result = slotsRulesHelpers.getWeekDays("wednesday");
        const expectedResult = [
            "wednesday",
            "thursday",
            "friday",
            "saturday",
            "sunday",
            "monday",
            "tuesday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a thursday", () => {
        const result = slotsRulesHelpers.getWeekDays("thursday");
        const expectedResult = [
            "thursday",
            "friday",
            "saturday",
            "sunday",
            "monday",
            "tuesday",
            "wednesday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a friday", () => {
        const result = slotsRulesHelpers.getWeekDays("friday");
        const expectedResult = [
            "friday",
            "saturday",
            "sunday",
            "monday",
            "tuesday",
            "wednesday",
            "thursday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a saturday", () => {
        const result = slotsRulesHelpers.getWeekDays("saturday");
        const expectedResult = [
            "saturday",
            "sunday",
            "monday",
            "tuesday",
            "wednesday",
            "thursday",
            "friday"
        ];
        expect(result).toEqual(expectedResult);
    });
    it("returns array of week days, starting on a sunday", () => {
        const result = slotsRulesHelpers.getWeekDays("sunday");
        const expectedResult = [
            "sunday",
            "monday",
            "tuesday",
            "wednesday",
            "thursday",
            "friday",
            "saturday"
        ];
        expect(result).toEqual(expectedResult);
    });
});
